PatchStack = {}
function UnitSizePatch(UnitID,Value,Table)
	table.insert(Table,SetMemory(0x6617C8 + (UnitID*8),SetTo,(Value)+(Value*65536)))
	table.insert(Table,SetMemory(0x6617CC + (UnitID*8),SetTo,(Value)+(Value*65536)))
end
